import React from "react";
import { useState, useEffect } from "react";
import Notas from "./packages/Notas";
import Loader from "./packages/Loader";
import { Container } from "react-bootstrap";

import styled from "styled-components";

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    //Fetch Tasks
    const fetchData = async () => {
      const content = getData(null);
      // console.log(data);
      setData(content); // return data;
    };
    fetchData();
  }, []);

  const getData = async (data) => {
    const res = await fetch("/negocios", {
      method: "POST",
      body: data,
    });
    const content = await res.json();
    return content;
  };

  const Styles = styled.div`
    .navbar {
      background-color: #222;
    }

    .navbar-brand,
    .navbar-nav,
    .nav-link {
      color: #bbb;

      &:hover {
        color: white;
      }
    }

    .nav-negocios {
      background-color: #888;
    }

    .row {
      background-color: #999;
      &.negocios {
        background-color: #9f9;
      }
    }
  `;

  return (
    <Styles>
      <Container fluid="md">
        <Loader getData={getData} />
        {
          data.length === 0 ? "Carregando nota..." : "Carregando 2"
          //data && <Notas notas={data} />
        }
      </Container>
    </Styles>
  );
};

export default App;
